mapper.insert:
INSERT INTO NOTICE_BOARD (
  NOTICE_BOARD_CODE
  , BOARD_SUBJECT
  , BOARD_CONTENT
  , BOARD_WRITER
) VALUES (
  #{noticeBoardCode}
  , #{boardSubject}
  , #{boardContent}
  , #{boardWriter}
)

mapper.select:
SELECT NOTICE_BOARD_CODE , BOARD_SUBJECT , BOARD_CONTENT , BOARD_WRITER , REG_DATE , ROW_NUM FROM ( SELECT NOTICE_BOARD_CODE , BOARD_SUBJECT , BOARD_CONTENT , BOARD_WRITER , REG_DATE , ROWNUM AS ROW_NUM FROM ( SELECT NOTICE_BOARD_CODE , BOARD_SUBJECT , BOARD_CONTENT , BOARD_WRITER , TO_CHAR(REG_DATE, 'YYYY.MM.DD') AS REG_DATE FROM NOTICE_BOARD
WHERE ${searchKeyword} LIKE '%'||#{searchValue}||'%'
ORDER BY NOTICE_BOARD_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}
