mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_EMAIL
FROM MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.insert:
INSERT INTO MEMBER (
MEMBER_CODE
  , MEMBER_ID
  , MEMBER_NAME
  , MEMBER_PW
  , MEMBER_TEL
  , MEMBER_ADDR
  , MEMBER_EMAIL
  , MEMBER_GENDER
) VALUES (
  (SELECT 'MEMBER_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MEMBER_CODE, 8, 3))), 0) + 1, 3, '0') FROM MEMBER)
  , #{memberId}
  , #{memberName}
  , #{memberPw}
  , #{memberTel}
  , #{memberAddr}
  , #{memberEmail}
  , #{memberGender}
)

mapper.select:
SELECT MEMBER_CODE
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PW = #{memberPw}

SELECT MEMBER_CODE , MEMBER_ID , MEMBER_NAME , MEMBER_JOIN_DATE , ROW_NUM FROM ( SELECT MEMBER_CODE , MEMBER_ID , MEMBER_NAME , MEMBER_JOIN_DATE , ROWNUM AS ROW_NUM FROM ( SELECT MEMBER_CODE , MEMBER_ID , MEMBER_NAME , TO_CHAR(MEMBER_JOIN_DATE, 'YYYY.MM.DD') AS MEMBER_JOIN_DATE FROM MEMBER WHERE MEMBER_IS_ADMIN = 'N'
AND ${searchKeyword} LIKE '%'||#{searchValue}||'%'
ORDER BY MEMBER_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.update:
UPDATE MEMBER
SET
MEMBER_PW = #{memberPw}
, MEMBER_EMAIL = #{memberEmail}
, MEMBER_TEL = #{memberTel}
, MEMBER_ADDR = #{memberAddr}
WHERE MEMBER_CODE = #{memberCode}
