    WHERE REVIEW_BOARD_CODE = #{reviewBoardCode}

mapper.insert:
INSERT INTO REVIEW_REPLY(
	REVIEW_REPLY_CODE
	, MEMBER_ID
	, REVIEW_REPLY_WRITER
	, REVIEW_REPLY_CONTENT
	, REVIEW_BOARD_CODE
) VALUES(
	(SELECT 'REVIEW_REPLY_'|| LPAD((NVL(MAX(TO_NUMBER(SUBSTR(REVIEW_REPLY_CODE,14,3))), 0) + 1), 3 ,'0') FROM REVIEW_REPLY)
	, #{memberId}
	, #{reviewReplyWriter}
	, #{reviewReplyContent}
	, #{reviewBoardCode}
)

mapper.select:
SELECT REVIEW_REPLY_CODE , REVIEW_REPLY_WRITER , REVIEW_REPLY_REG_DATE , REVIEW_REPLY_CONTENT , REVIEW_BOARD_CODE , ROW_NUM FROM ( SELECT REVIEW_REPLY_CODE , REVIEW_REPLY_WRITER , REVIEW_REPLY_REG_DATE , REVIEW_REPLY_CONTENT , REVIEW_BOARD_CODE , ROWNUM AS ROW_NUM FROM ( SELECT REVIEW_REPLY_CODE , REVIEW_REPLY_WRITER , TO_CHAR(REVIEW_REPLY_REG_DATE, 'YYYY.MM.DD') AS REVIEW_REPLY_REG_DATE , REVIEW_REPLY_CONTENT , REVIEW_BOARD_CODE FROM REVIEW_REPLY
WHERE ${searchKeyword} LIKE '%'||#{searchValue}||'%'
ORDER BY REVIEW_REPLY_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

SELECT COUNT(REVIEW_REPLY_CODE)
FROM REVIEW_REPLY
WHERE REVIEW_BOARD_CODE = #{reviewBoardCode}

mapper.update:
UPDATE REVIEW_BOARD
SET REVIEW_BOARD_RECOMMEND_CNT = REVIEW_BOARD_RECOMMEND_CNT + 1
WHERE REVIEW_BOARD_CODE = #{reviewBoardCode}

mapper.insert:
